import React, { useState } from 'react';
import './App.css';
import ProductList from './ProductList';

const App = () => {
  const [cart, setCart] = useState([]);
  const products = [
    {
      id: 1,
      name: 'Laptop',
      description: 'Powerful desktop for your computing needs',
      price: 1299,
      image: 'data:image/jpeg;base64,',
    },
    {
      id: 2,
      name: 'Mobile',
      description: 'Portable laptop for work and play',
      price: 999,
      image: 'data:image/jpeg;base64,',
    },
    {
      id: 3,
      name: 'spectacles',
      description: 'Latest smartphone with amazing features',
      price: 599,
      image: 'data:image/png;base64,',
    },
    {
      id: 4,
      name: 'Earphones',
      description: 'High-quality earphones for immersive audio',
      price: 49,
      image: 'data:image/jpeg;base64,',
    },
    {
      id: 5,
      name: 'Holders',
      description: 'Exciting gadgets to enhance your digital life',
      price: 199,
      image: 'data:image/jpeg;base64,',
    },
  ];

  const addToCart = (product) => {
    setCart([...cart, product]);
    displayMessage('add', product);
  };

  const removeFromCart = (productToRemove) => {
    setCart(cart.filter((product) => product.id !== productToRemove.id));
    displayMessage('remove', productToRemove);
  };

  const displayMessage = (type, product) => {
    const emoji = type === 'add' ? '🎉' : '❌';
    const action = type === 'add' ? 'added to' : 'removed from';
    const message = `Thank you for ${action} your cart! ${emoji}`;
    alert(message);
  };

  return (
    <div className="container">
      <h1>Modern Tech Shop</h1>
      <ProductList products={products} addToCart={addToCart} removeFromCart={removeFromCart} />
      <div className="shopping-cart">
        <h2>Shopping Cart</h2>
        <ul>
          {cart.map((product) => (
            <li key={product.id}>
              {product.name} - ${product.price}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default App;
